const Quantum = require('quantum-framework');


Quantum.validate.All([{
            string: '12345',
            method: 'isLength',
            options: { min: 5, max: 12 }
        },
        {
            string: '12345566',
            method: 'isLength',
            options: { min: 6, max: undefined }
        },
    ])
    .then(function(result) {
        console.log(result);
    })
    .catch(function(err) {
        console.log(err);
    });